export const ONE_SECOND = 1 * 1_000;
export const TWO_SECONDS = 2 * ONE_SECOND;
export const FIVE_SECONDS = 5 * ONE_SECOND;
export const TEN_SECONDS = 10 * ONE_SECOND;
export const TWENTY_SECONDS = 20 * ONE_SECOND;
 
export const ONE_MINNUTE = 60 * ONE_SECOND;
export const TWO_MINNUTES = 2 * ONE_MINNUTE;
export const FIVE_MINNUTES = 5 * ONE_MINNUTE;
export const TEN_MINNUTES = 10 * ONE_MINNUTE;
export const TWENTY_MINNUTES = 20 * ONE_MINNUTE;

export const ONE_HOUR = 60 * ONE_MINNUTE;
export const TWO_HOURS = 2 * ONE_HOUR;
export const FIVE_HOURS = 5 * ONE_HOUR;
export const TEN_HOURS = 10 * ONE_HOUR;
export const TWENTY_HOURS = 20 * ONE_HOUR; 